INSERT INTO `table_product` VALUES (217,84,115,0,0,'','san-pham',1,0,1,0,1,'co-len-thang-cap-2_11zon_1734707555716.webp','co-len-thang-cap-2648-1288_540x540.jpg','','','','co-len-thang-cap','Co Lên Thang Cáp ','Riser Elbow','','','<p style=\"text-align: justify; line-height: 1.5\">Co lên thang cáp là phụ kiện giúp chuyển hướng hệ thống cáp điện từ thấp lên cao theo phương đứng, phù hợp với các vị trí giao tầng, chuyển cốt hoặc vượt chướng ngại. Thiết kế chắc chắn, dễ lắp đặt, đảm bảo đồng bộ và an toàn cho hệ thống thang cáp.</p><h2 style=\"text-align: justify; line-height: 1.5\"><span style=\"font-size: 18px\"><strong>ĐẶC ĐIỂM SẢN PHẨM:</strong></span></h2><ul><li><p style=\"text-align: justify; line-height: 1.5\"><strong>Kích thước tiêu chuẩn:</strong> từ 50mm đến 1000mm, mã TC-CL01 đến TC-CL13.</p></li><li><p style=\"text-align: justify; line-height: 1.5\"><strong>Bề mặt hoàn thiện:</strong> sơn tĩnh điện, mạ kẽm điện phân hoặc nhúng nóng.</p></li><li><p style=\"text-align: justify; line-height: 1.5\"><strong>Chất liệu:</strong> tôn sơn tĩnh điện, tôn mạ kẽm hoặc inox 304 – độ bền cao.</p></li></ul>','','<p style=\"text-align: justify; line-height: 1.5\"><a target=\"_blank\" rel=\"noopener noreferrer\" href=\"https://zencomex.com/co-len-thang-cap\"><span style=\"color: rgb(51, 51, 255)\"><strong>Co lên thang cáp</strong></span></a> là phụ kiện được sử dụng để điều hướng hệ thống dây cáp đi lên theo phương thẳng đứng. Phụ kiện này đặc biệt cần thiết tại các vị trí giao tầng, khu vực có thay đổi cao độ hoặc dẫn cáp từ tầng thấp lên tầng cao. Sản phẩm được ứng dụng rộng rãi trong thi công hệ thống điện – M&amp;E tại nhà máy, tòa nhà cao tầng, trạm kỹ thuật, trung tâm dữ liệu.</p><p style=\"text-align: justify; line-height: 1.5\">Sản phẩm được sản xuất theo tiêu chuẩn kỹ thuật cao, đồng bộ với các mã <a target=\"_blank\" rel=\"noopener noreferrer\" href=\"https://zencomex.com/thang-cap\"><span style=\"color: rgb(51, 51, 255)\"><strong>thang cáp</strong></span></a> TC-CL01 đến TC-CL13, đảm bảo lắp đặt nhanh chóng, không cần gia công tại công trường. Thiết kế dạng bậc giúp cố định cáp tốt hơn, giảm võng dây và tạo thuận lợi khi bảo trì.</p><h2 style=\"line-height: 1.5\"><span style=\"font-size: 18px\"><strong>Thông số kỹ thuật co lên thang cáp</strong></span></h2><ul><li><p style=\"line-height: 1.5\"><strong>Chiều rộng W:</strong> từ 100mm – 1000mm</p></li><li><p style=\"line-height: 1.5\"><strong>Chiều cao H:</strong> từ 50 – 100mm</p></li><li><p style=\"line-height: 1.5\"><strong>Chiều dài L1 / L2:</strong> 300 – 450mm theo từng mã</p></li><li><p style=\"line-height: 1.5\"><strong>Độ dày tôn:</strong> 1.0 – 2.0mm</p></li><li><p style=\"line-height: 1.5\"><strong>Mã sản phẩm:</strong> TC-CL01 đến TC-CL13</p></li></ul><img style=\"margin: 0 auto; display: block; width: 100%; height: auto; cursor: pointer\" src=\"https://zencomex.com/api/upload/product/editor/co-len-thang-cap_1752458990893.webp\" alt=\"Thông số kỹ thuật co lên thang cáp\" title=\"Thông số kỹ thuật co lên thang cáp\" draggable=\"true\"><ul><li><p style=\"line-height: 1.5\"><strong>Chất liệu:</strong></p><ul><li><p style=\"line-height: 1.5\">Tôn sơn tĩnh điện</p></li><li><p style=\"line-height: 1.5\">Tôn mạ kẽm (điện phân / nhúng nóng)</p></li><li><p style=\"line-height: 1.5\">Inox 304 – phù hợp môi trường ăn mòn</p></li></ul></li><li><p style=\"line-height: 1.5\"><strong>Xử lý bề mặt:</strong></p><ul><li><p style=\"line-height: 1.5\">Sơn epoxy tĩnh điện màu tùy chọn</p></li><li><p style=\"line-height: 1.5\">Mạ kẽm điện phân hoặc mạ kẽm nhúng nóng</p></li></ul></li></ul><h2 style=\"line-height: 1.5\"><span style=\"font-size: 18px\">Ứng dụng thực tế co lên thang cáp</span></h2><ul><li><p style=\"text-align: justify; line-height: normal\">Thi công hệ thống thang cáp điện chuyển tầng tại các công trình cao tầng, trung tâm thương mại, nhà xưởng.</p></li><li><p style=\"text-align: justify; line-height: normal\">Dẫn cáp đi lên tại vị trí vượt sàn, vách kỹ thuật hoặc lên trần treo.</p></li><li><p style=\"text-align: justify; line-height: normal\">Giúp hệ thống thang cáp vận hành ổn định, dễ kiểm tra, bảo trì và đảm bảo thẩm mỹ.</p></li></ul><p style=\"text-align: justify; line-height: normal\"><strong>xem thêm:</strong><em> </em><a target=\"_blank\" rel=\"noopener noreferrer\" href=\"https://zencomex.com/co-xuong-thang-cap\"><span style=\"color: rgb(51, 51, 255)\"><em>co xuống thang cáp</em></span></a></p><h2 style=\"line-height: normal\"><span style=\"font-size: 18px\"><strong>Cam kết từ Zencomex</strong></span></h2><ul><li><p style=\"line-height: normal\">Sản phẩm đạt tiêu chuẩn TCVN 10688 – 2015 / IEC 61537 – 2006</p></li><li><p style=\"line-height: normal\">Hỗ trợ tư vấn kỹ thuật – thiết kế layout lắp đặt – cung cấp đầy đủ CO–CQ</p></li><li><p style=\"line-height: normal\">Giao hàng nhanh – báo giá linh hoạt theo số lượng – hỗ trợ trọn gói dự án</p></li></ul>','<p><img alt=\"\" height=\"944\" src=\"https://zencomex.com/upload/images/Screen%20Shot%202023-02-21%20at%2016_20_42.png\" width=\"1368\" /></p>\r\n',0,0,'Mua Co Lên Thang Cáp ZCG | Giá Tốt Nhất, Giao Hàng Nhanh ','co lên, phụ kiện co lên thang cáp, co lên thang cáp, thang cáp','Co lên thang cáp mạ kẽm, sơn tĩnh điện, chất lượng cao. Giá cạnh tranh, giao hàng nhanh, đáp ứng mọi yêu cầu thi công hệ thống điện liên hệ nhận báo giá ngay.',0,7,597,1587973662,1676971002,''),(228,0,131,0,0,'','san-pham',1,0,0,0,1,'kep-maxseam-1_1734920680222.webp','kep-bia-tam-pin-2706-8149_540x540.jpg','','','','kep-maxseam','Kẹp Maxseam Dùng Cho Tole Seamlock','End Clamp','','','<p style=\"text-align: justify; line-height: 1.5\">Kẹp Maxseam là phụ kiện chuyên dụng trong hệ thống điện mặt trời, được thiết kế để cố định chắc chắn trên mái tôn Seamlock (Lockseam) mà không cần đục thủng bề mặt mái. Giải pháp này giúp đảm bảo tính thẩm mỹ, hạn chế thấm dột và tăng độ bền cho công trình. Kẹp Maxseam dễ dàng lắp đặt, phù hợp cho các công trình mái vượt khổ, thi công nhanh chóng, an toàn và ổn định trong thời gian dài.</p><h2 style=\"text-align: justify; line-height: 1.5\"><span style=\"font-size: 20px\"><strong>Đặc điểm </strong></span><span style=\"font-size: 20px\">kẹp maxseam</span><span style=\"font-size: 20px\"><strong>:</strong></span></h2><ul><li><p style=\"text-align: justify; line-height: 1.5\"><strong>Vật liệu</strong>: Nhôm AL6005-T5, bulong inox 304</p></li><li><p style=\"text-align: justify; line-height: 1.5\"><strong>Bề mặt xử lý</strong>: Anodized chống oxy hóa, ăn mòn</p></li><li><p style=\"text-align: justify; line-height: 1.5\"><strong>Tiêu chuẩn</strong>: AS/NZS 1170.2 &amp; JIS C 8955:2011</p></li></ul>','','<p style=\"text-align: justify; line-height: 1.5\"><span style=\"color: rgb(2, 2, 2); font-size: 16px\"><strong>​</strong></span><span style=\"color: rgb(2, 2, 2)\"><strong>Kẹp Maxseam</strong></span> là <a target=\"_blank\" rel=\"noopener noreferrer\" href=\"https://zencomex.com/phu-kien-solar\"><span style=\"color: rgb(51, 51, 255)\"><strong>phụ kiện solar</strong></span></a> không thể thiếu trong hệ thống điện mặt trời gắn trên <strong>mái tôn seamlock</strong> – loại mái tôn không sử dụng vít cố định, thường dùng cho công trình có diện tích lớn, yêu cầu độ bền cao và chống thấm tuyệt đối. Việc sử dụng <a target=\"_blank\" rel=\"noopener noreferrer\" href=\"https://zencomex.com/kep-maxseam\"><span style=\"color: rgb(51, 51, 255)\"><strong>kẹp Maxseam</strong></span></a> giúp hệ thống năng lượng mặt trời được lắp đặt an toàn, ổn định, không ảnh hưởng đến kết cấu mái, phù hợp với xu hướng thi công sạch – không xâm lấn.</p><img style=\"margin: 0 auto; display: block; width: 100%; height: auto; cursor: pointer\" src=\"https://zencomex.com/api/upload/product/editor/kep-maxseam_1752463267250.webp\" alt=\"Thông số kỹ thuật kẹp Maxseam\" title=\"Thông số kỹ thuật kẹp Maxseam\" draggable=\"true\"><h2 style=\"line-height: 1.5\"><span style=\"font-size: 20px\"><strong>Thông số kỹ thuật của </strong>​kẹp maxseam</span></h2><ul><li><p style=\"line-height: 1.5\"><strong>Chất liệu kẹp</strong>: Nhôm AL6005-T5</p></li><li><p style=\"line-height: 1.5\"><strong>Bulong</strong>: Inox 304 M8x25mm</p></li><li><p style=\"line-height: 1.5\"><strong>Bề mặt</strong>: Anodized chống ăn mòn</p></li><li><p style=\"line-height: 1.5\"><strong>Chịu lực</strong>: 1,4KN/m², sức gió tới 60m/s</p></li><li><p style=\"line-height: 1.5\"><strong>Tiêu chuẩn</strong>: AS/NZS 1170.2 và JIS C 8955:2011</p></li><li><p style=\"line-height: 1.5\"><strong>Bộ sản phẩm gồm</strong>:</p><ul><li><p style=\"line-height: 1.5\">01 Miếng kẹp Maxseam</p></li><li><p style=\"line-height: 1.5\">02 <a target=\"_blank\" rel=\"noopener noreferrer\" href=\"https://zencomex.com/bulong-dai-oc\"><span style=\"color: rgb(51, 51, 255)\"><strong>bulong</strong></span></a> M8x25 inox 304</p></li><li><p style=\"line-height: 1.5\">02 tán M8 inox 304</p></li><li><p style=\"line-height: 1.5\">02 lông đền vênh + 02 lông đền phẳng</p></li></ul></li></ul><h2 style=\"line-height: 1.5\"><span style=\"font-size: 20px\">Ưu điểm và ứng dụng của ​kẹp maxseam</span></h2><ul><li><p style=\"text-align: justify; line-height: 1.5\"><strong>Không khoan mái, </strong>không ảnh hưởng đến lớp phủ chống thấm, phù hợp mái tôn seamlock hiện đại.</p></li><li><p style=\"text-align: justify; line-height: 1.5\">Giảm hiện tượng co giãn nhiệt làm rạn nứt tấm pin nhờ kết cấu chắc chắn, đàn hồi tốt.</p></li><li><p style=\"text-align: justify; line-height: 1.5\">Dễ dàng thi công và <strong>tiết kiệm nhân công</strong>, rút ngắn thời gian lắp đặt.</p></li><li><p style=\"text-align: justify; line-height: 1.5\">Tương thích tốt với các loại<strong> </strong><a target=\"_blank\" rel=\"noopener noreferrer\" href=\"https://zencomex.com/thanh-rail-nhom\"><span style=\"color: rgb(51, 51, 255)\"><strong>thanh rail nhôm</strong></span></a>, đảm bảo độ an toàn và thẩm mỹ cho hệ thống điện mặt trời.</p></li><li><p style=\"text-align: justify; line-height: 1.5\">Ứng dụng phổ biến trong: <strong>dự án điện mặt trời áp mái dân dụng, nhà xưởng, nhà máy công nghiệp</strong>, đặc biệt với mái tôn seamlock cán tại công trình.</p></li></ul>','<p><img alt=\"\" height=\"542\" src=\"https://zencomex.com/upload/images/Screen%20Shot%202023-02-21%20at%2016_47_15.png\" width=\"1600\" /></p>\r\n',0,0,'Kẹp Maxseam Dùng Cho Tole Seamlock - Phụ Kiện Solar ','Kẹp Maxseam, phụ kiên solar','Kẹp Maxseam cho tấm tôn Seamlock, chất liệu thép không gỉ, bền bỉ, dễ lắp đặt, phù hợp công trình mái tôn và năng lượng mặt trời. Giá tốt nhất!',0,1,250,1603538699,1676972585,'admin'),(229,0,131,0,0,'','san-pham',1,0,0,0,1,'kep-tiep-dia-thanh-rail-nhom-1_1734921845547.webp','kep-tiep-dia-4216-2665_540x540.jpg','','','','kep-tiep-dia','Kẹp Tiếp Địa - Phụ Kiện Solar','rail Clip','','','<p style=\"text-align: justify; line-height: 1.5\">Kẹp tiếp địa thanh rail là <a target=\"_blank\" rel=\"noopener noreferrer\" href=\"https://zencomex.com/phu-kien-solar\"><span style=\"color: #3333ff\"><strong>phụ kiện solar</strong></span></a> thiết yếu trong hệ thống điện mặt trời, giúp kết nối hiệu quả giữa module pin – thanh ray nhôm – hệ thống tiếp địa. Sản phẩm góp phần đảm bảo an toàn điện, bảo vệ con người và thiết bị khỏi rò rỉ dòng điện hoặc sét lan truyền. Với thiết kế nhỏ gọn, dễ lắp đặt và độ bền vượt trội, kẹp tiếp địa là giải pháp tối ưu cho hệ thống solar chuyên nghiệp.</p><p style=\"text-align: justify; line-height: 1.5\"><strong>Đặc điểm sản phẩm:</strong></p><ul><li><p style=\"text-align: justify; line-height: 1.5\"><strong>Vật liệu</strong>: Nhôm AL6005-T5, thép không gỉ SUS304</p></li><li><p style=\"text-align: justify; line-height: 1.5\"><strong>Bề mặt xử lý</strong>: Anodized chống oxy hóa</p></li><li><p style=\"text-align: justify; line-height: 1.5\"><strong>Tiêu chuẩn</strong>: AS/NZS 1170.2 &amp; JIS C 8955:2011</p></li></ul>','','<p style=\"text-align: justify; line-height: 1.5\">Trong hệ thống điện mặt trời, an toàn điện luôn được đặt lên hàng đầu – và kẹp tiếp địa thanh rail chính là một trong nhữ