INSERT INTO `table_product_cat` VALUES (54,192,'san-pham','Hộp nối ống','','','','hop-noi-ong','','','','','','','',6,1,1,1603505526,0,NULL),(53,192,'san-pham','Đầu nối ống','','','','dau-noi-ong','<p style=\"line-height: 1.5\"><span style=\"font-size: 16px\">​</span></p>','','Đầu Nối Ống – Kết Nối Ổn Định, Lắp Đặt Nhanh Chóng','','Đầu nối ống chất lượng cao, dễ lắp đặt, kết nối chắc chắn, chống rò rỉ. Ứng dụng trong hệ thống cấp thoát nước, khí nén, và đường ống công nghiệp.','','',5,1,1,1603505490,0,NULL),(52,192,'san-pham','Co cong, co đúc','','','','co-cong-co-duc','','','','','','','',4,1,0,1603436174,0,NULL),(51,192,'san-pham','Hộp thép đấu dây','','','','hop-thep-dau-day','<p style=\"line-height: 1.5\"><span style=\"font-size: 16px\">​</span></p>','','Hộp Thép Đấu Dây Chống Gỉ – Bền Bỉ, An Toàn, Giá Tốt Nhất','','Hộp thép đấu dây điện chất lượng cao, chống gỉ sét, chịu lực tốt. Có sẵn nhiều kích thước, giao hàng nhanh toàn quốc – Liên hệ nhận báo giá hôm nay!','','',3,1,1,1603436150,0,NULL),(90,127,NULL,'Xe Đẩy Nha Khoa',NULL,NULL,NULL,'xe-day-nha-khoa-3-tang','<h2 style=\"line-height: [object Object]\"><span style=\"font-size: 20px\"><strong>XE ĐẨY NHA KHOA LÀ GÌ?</strong></span></h2><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">Xe đẩy nha khoa là một thiết bị di động được sử dụng trong phòng mạch nha khoa để chuyển đồ và vật dụng giữa các khu vực khác nhau trong phòng làm việc. Thông thường, xe đẩy này được thiết kế để chứa các dụng cụ và vật liệu nha khoa cần thiết như máy khoan, hút nước, dụng cụ làm sạch và các vật liệu phục vụ điều trị nha khoa.</span></p><img style=\"margin: 0 auto; display: block; width: 800px; height: auto; cursor: pointer\" src=\"https://zencomex.com/api/upload/images/Xe%20%C4%91%E1%BA%A9y%20nha%20khoa%203%20t%C3%A2ng.jpg\" alt=\"\" width=\"800\" height=\"800\" class=\"fr-fic fr-dii\" draggable=\"true\"><p style=\"line-height: normal\">xe đẩy nha khoa 3 tầng</p><p style=\"text-align: center; line-height: [object Object]\">&nbsp;</p><h3 style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\"><strong>ĐẶC ĐIỂM CỦA XE ĐẨY NHA KHOA</strong></span></h3><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">Đặc điểm của xe đẩy nha khoa bao gồm bánh xe di chuyển linh hoạt, khay hoặc ngăn đựng đa dạng để tổ chức vật liệu và thiết bị một cách thuận tiện. Điều này giúp nha sĩ và nhân viên nha khoa có thể tiếp cận các công cụ một cách dễ dàng trong quá trình điều trị bệnh nhân.</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">Xe đẩy nha khoa không chỉ giúp tăng cường hiệu suất làm việc mà còn đảm bảo sự sạch sẽ và tổ chức trong phòng nha khoa. Điều này giúp tối ưu hóa quy trình làm việc và tạo điều kiện thuận lợi cho việc cung cấp dịch vụ chăm sóc nha khoa chất lượng cao.</span></p><p style=\"text-align: center; line-height: [object Object]\"></p><p style=\"text-align: center; line-height: [object Object]\">&nbsp;</p><p style=\"text-align: center; line-height: [object Object]\">&nbsp;</p><h3 style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\"><strong>CHẤT LIỆU XE ĐẨY NHA KHOA</strong></span></h3><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">Xe đẩy nha khoa được là từ chát liệu thép không rỉ &nbsp;(Stainless Steel):</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">- Thép không gỉ là một lựa chọn phổ biến do khả năng chống ăn mòn và dễ lau chùi. Nó thường được sử dụng trong môi trường nha khoa nơi có thể tiếp xúc với nước và các chất hóa học.</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">Xe đẩy nha khoa được sơn lên một lớp sơn tĩnh điện:</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">- sơn tĩnh điện là một phương pháp phổ biến để cải thiện tính năng chống ăn mòn và tăng cường vẻ ngoại hình của xe đẩy nha khoa. Dưới đây là một số lợi ích của việc sử dụng lớp sơn tĩnh điện trên xe đẩy:</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">- Chống ăn mòn: Lớp sơn tĩnh điện giúp bảo vệ bề mặt của xe đẩy khỏi ảnh hưởng của môi trường, đặc biệt là ẩm ướt và các chất hóa học trong môi trường nha khoa.</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">- Tăng cường độ bền: Sơn tĩnh điện có khả năng tạo ra lớp phủ mạnh mẽ và bền, giúp tăng cường độ bền của xe đẩy và ngăn chặn sự trầy xước và va đập.</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">- Tính thẩm mỹ: Việc sử dụng lớp sơn tĩnh điện giúp tạo nên một vẻ ngoại hình chuyên nghiệp và hiện đại cho xe đẩy, đồng thời tạo sự đồng nhất về màu sắc.</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">- Dễ lau chùi: Lớp sơn tĩnh điện thường có tính chất dễ lau chùi, giúp bảo quản vẻ sạch sẽ và chuyên nghiệp của xe đẩy nha khoa.</span>&nbsp;</p><h3 style=\"line-height: [object Object]\"><span style=\"font-family: &quot;segoe ui symbol&quot;, sans-serif\"><strong>⬤</strong></span><span style=\"color: rgb(51, 51, 51)\"><strong>&nbsp;ƯU ĐIỂM CỦA XE ĐẨY NHA KHOA:</strong></span></h3><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">- Đa Dạng Chức Năng: Thiết kế để đựng và tổ chức máy cắm Implant, máy cắt xương, máy đốt cao tần và các dụng cụ nha khoa khác, giúp tạo ra môi trường làm việc tiện lợi cho bác sĩ.</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">- Kiểu Dáng Sang Trọng: Thiết kế hiện đại và sang trọng, không chỉ mang lại tính thẩm mỹ mà còn tạo ấn tượng chuyên nghiệp trong phòng mạch nha khoa.</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">- Nâng Cao Năng Suất: Hỗ trợ nâng cao hiệu suất làm việc cho bác sĩ bằng cách giữ cho dụng cụ và máy móc được tổ chức gọn gàng và dễ dàng tiếp cận.</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">- Chất Liệu Chất Lượng Cao: Sử dụng chất liệu thép, được phủ lớp sơn tĩnh điện, giúp bảo vệ khỏi ảnh hưởng của môi trường và tăng cường độ bền.</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">- Cấu Trúc Linh Hoạt: Có thể có cấu trúc liền khối hoặc tháo rời, tùy thuộc vào nhu cầu sử dụng và không gian trong phòng mạch.</span>&nbsp;</p><h3 style=\"line-height: [object Object]\"><span style=\"font-size: 15pt\"><strong>⬤&nbsp;Kích Thước và Trọng Lượng:</strong></span></h3><ul><li><p style=\"line-height: normal\"><span style=\"font-size: 16px\">&nbsp;Chiều Cao: 74cm.</span></p></li><li><p style=\"line-height: normal\"><span style=\"font-size: 16px\">&nbsp;Bàn Đựng Thiết Bị 1: 32 x 43 cm.</span></p></li><li><p style=\"line-height: normal\"><span style=\"font-size: 16px\">&nbsp;Bàn Đựng Thiết Bị 2: 24 x 35 cm.</span></p></li><li><p style=\"line-height: normal\"><span style=\"font-size: 16px\">&nbsp;Bàn Đựng Thiết Bị 3: 40 x 40 cm.</span></p></li><li><p style=\"line-height: normal\"><span style=\"font-size: 16px\">Khối Lượng: 20kg.</span></p></li></ul><h2 style=\"line-height: [object Object]\"><span style=\"font-size: 20px\"><strong>SAU ĐÂY LÀ 4 MẪU XE ĐẨY CỦA ZENCOMEX:</strong></span></h2><h2 style=\"line-height: [object Object]\"><span style=\"font-family: &quot;segoe ui symbol&quot;, sans-serif\"><strong>⬤</strong></span><span style=\"color: rgb(51, 51, 51)\"><strong>&nbsp;Mẫu Xe ZCG01 - Cấu Trúc 3 Tầng Liền Khối:</strong></span></h2><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\">&nbsp;Cấu Trúc:</span></p><p style=\"line-height: [object Object]\"><span style=\"color: rgb(51, 51, 51)\"><strong>1.&nbsp;</strong></span><span style=\"color: red\"><strong>Tầng 1 (Bàn Đựng Thiết Bị 1):</strong></span></p><ul><li><p style=\"line-height: normal\"><span style=\"font-size